package com.cos.blog.config;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.security.config.annotation.method.configuration.EnableGlobalMethodSecurity;import org.springframework.security.config.annotation.rsocket.EnableRSocketSecurity;import org.springframework.security.config.annotation.web.builders.HttpSecurity;import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;import org.springframework.security.config.annotation.web.configuration.WebSecurityConfigurerAdapter;import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;//빈등록 : 스플이 컨테이너에서 객체 관리를 할 수 있게 하는것.@Configuration // 빈등록(ioc관리)@EnableWebSecurity // 시큐리티 필터가 등록이 된다.@EnableGlobalMethodSecurity(prePostEnabled = true) // 특정 주소로 접근 하면 권한 및 인증을 미리 체크 하겠따는 뜻.public class SecurityConfig extends WebSecurityConfigurerAdapter {   @Bean  // IOC   public BCryptPasswordEncoder encodePWD(){      return new BCryptPasswordEncoder();   }   @Override   protected void configure(HttpSecurity http) throws Exception{   http          .csrf().disable() // csrf 토큰 비활성화           .authorizeRequests()          .antMatchers("/", "/auth/**", "/js/**", "/css/**", "/image/**")          .permitAll()          .anyRequest() // 이게 아닌 다른 모든 요청은          .authenticated() //인증이 되어야 해..and()          .formLogin()          .loginPage("/auth/loginForm");   }}