package com.cos.blog.handler;import com.cos.blog.dto.ResponseDto;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.RestController;//404번의 대한 에러보다 이렇게 exception의 대한 구현을 통해 심플한 개발환경을 만들 수 있다.@ControllerAdvice@RestControllerpublic class GlobalExceptionHandler {   @ExceptionHandler(value =Exception.class) // exception의 대한   public ResponseDto<String> handlerArgumentException(Exception e){      return             new ResponseDto<String>(HttpStatus.INTERNAL_SERVER_ERROR.value(), e.getMessage());   }}